import React from 'react';
import Card from './movie/Card';

class PopularBattle extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			currentPage: 1,
			movies: []
		};
	}

	componentDidMount() {
		let API_KEY = '74e7d2a525818dc6d851a13d76d92d3c';
		let url = `https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=${API_KEY}`;
		fetch(url).then((res) => res.json()).then((json) => {
			console.log(' Popular, vérifier le contenu de json', json);
			this.setState({
				movies: json.results
			});
		});
	}

	onClickMovies(movieId) {
		this.setState({
			currentPage: this.state.currentPage + 1
		});
	}

	render() {
		console.log('popularBattle afficher le state movies', this.state.movies);
		let movieOne = this.state.movies[(this.state.currentPage - 1) * 2];
		let movieTwo = this.state.movies[(this.state.currentPage - 1) * 2 + 1];
		console.log('popularBattle movieOne', typeof movieOne);
		console.log('popularBattle movieOne', movieOne['poster_path']);
		console.log('popularBattle movieTwo', movieTwo);

		return (
			<div className="container">
				{/* <div className="row">
					<div className="col-6">
						<Card image={movieOne.poster_path} title={movieOne.title} description={movieOne.overview} />
					</div>
					<div className="col-6">
						<Card image={movieTwo.poster_path} title={movieTwo.title} description={movieTwo.overview} />
					</div>
				</div> */}
			</div>
		);
	}
}

export default PopularBattle;
